import React, { Component } from "react";
import { Button, Form, Grid, Header, Image, Message, Segment } from 'semantic-ui-react'
import firebase from 'firebase/app';
//import { auth } from 'firebase/app';

//const API_KEY = process.env.REACT_APP_API_KEY;
//console.log(API_KEY);
export class Auth extends Component {
  constructor(props) {
    super(props);

    var config = {
      apiKey: process.env.REACT_APP_API_KEY,
      authDomain: "authfirebase-17e0d.firebaseapp.com",
      databaseURL: "https://authfirebase-17e0d.firebaseio.com",
      projectId: "authfirebase-17e0d",
      storageBucket: "authfirebase-17e0d.appspot.com",
      messagingSenderId: "57933939977",
      appId: "1:57933939977:web:e70901c703d64571"
    };

    firebase.initializeApp(config);

    this.state = {
        username: "",
        password: "",
        error: "",
        loginData: {}
      };
    this.handlePassChange = this.handlePassChange.bind(this);
    this.handleUserChange = this.handleUserChange.bind(this);






    }


  render() {
    return <Grid textAlign='center' style={{ height: '100vh' }} verticalAlign='middle'>
    <Grid.Column style={{ maxWidth: 450 }}>
    <Message>
      <Header as='h3' textAlign='center' >
        Log-in to your account
      </Header></Message>
      <Form size='large'>
        <Segment stacked>
          <Form.Input 
            fluid icon='user' 
            iconPosition='left' 
            placeholder='E-mail address' 
            value={this.state.username}
            onChange={this.handleUserChange} />
          <Form.Input
            fluid
            icon='lock'
            iconPosition='left'
            placeholder='Password'
            type='password'
            value={this.state.password}
            onChange={this.handlePassChange}

          />

          <Button color='red' fluid size='large' onClick={this.login}>
            Login
          </Button>
        </Segment>
      </Form>
      <Message>
        New to us? <a href='#'>Sign Up</a>
      </Message>
    </Grid.Column>
  </Grid>;
  }
}

export default Auth;
