import React, { Component } from "react";
import { Button, Message } from "semantic-ui-react";

window.SpeechRecognition =
  window.webkitSpeechRecognition || window.SpeechRecognition;
let finalTranscript = "";
let recognition = new window.SpeechRecognition();
recognition.interimResults = true;
recognition.maxAlternatives = 10;
recognition.continuous = true;
export class Speech extends Component {
  state = { pressed: true, res: "" };
  btnPressed = () => {
    this.setState({ pressed: !this.state.pressed });
    recognition.onresult = (event) => {
      let interimTranscript = '';
      for (let i = event.resultIndex, len = event.results.length; i < len; i++) {
        let transcript = event.results[i][0].transcript;
        if (event.results[i].isFinal) {
          finalTranscript += transcript;
        } else {
          interimTranscript += transcript;
        }
      }
      this.setState({res:finalTranscript  + interimTranscript});
    }
    recognition.start();

  };
  render() {
    console.log(this.state.res);
    return (
      <div>
        <Button
          content={this.state.pressed ? "Start Record" : "Recording"}
          icon="microphone"
          color={this.state.pressed ? "teal" : "red"}
          labelPosition="left"
          onClick={this.btnPressed}
        />
        <Message>
          <Message.Header>Result</Message.Header>
          <p>{this.state.res}</p>
        </Message>
      </div>
    );
  }
}

export default Speech;
