import React, { Component } from "react";
import PropTypes from "prop-types";
import {
  Button,
  Checkbox,
  Grid,
  Header,
  Icon,
  Image,
  Menu,
  Segment,
  Sidebar
} from "semantic-ui-react";

const VerticalSidebar = ({ animation, direction, visible }) => (
  <Sidebar
    as={Menu}
    animation={animation}
    direction={direction}
    icon="labeled"
    inverted
    vertical
    visible={visible}
    width="thin"
  >
    <Menu.Item as="a">
      <Icon name="home" />
      Home
    </Menu.Item>
    <Menu.Item as="a">
      <Icon name="gamepad" />
      Games
    </Menu.Item>
    <Menu.Item as="a">
      <Icon name="camera" />
      Channels
    </Menu.Item>
  </Sidebar>
);

VerticalSidebar.propTypes = {
  visible: PropTypes.bool
};

export class RevCode extends Component {
  state = {
    dimmed: false,
    visible: false
  };

  render() {
    const { dimmed, visible } = this.state;
    console.log(this.state)
    return (
      <div>
        <Button
          onclick={e => {
            this.setState((prevState) => ({ visible: !prevState.visible }));
          }}
        >
          Show
        </Button>
        <Sidebar.Pushable as={Segment}>
          <VerticalSidebar
            animation="overlay"
            direction="left"
            visible={visible}
          />

          <Sidebar.Pusher dimmed={dimmed && visible}>
            <Segment basic>
              <Header as="h3">Application Content</Header>
              <Image src="/images/wireframe/paragraph.png" />
            </Segment>
          </Sidebar.Pusher>
        </Sidebar.Pushable>
      </div>
    );
  }
}

export default RevCode;
