import React, { Component } from "react";
import { Button, Message } from "semantic-ui-react";

var recognition = new window.webkitSpeechRecognition();
let speech = "";
var forcestop = false;
let startTimer = 0;
let timer = false;
var Interval;
export class Speech extends Component {
  state = { btnStyle: true, btnText: true,res:"" };
  btnPressed = () => {
    this.setState({ btnStyle: !this.state.btnStyle });
    this.setState({ btnText: !this.state.btnText });
    clearInterval(Interval);
        Interval = setInterval(startTimer, 10);
        timer = true;
    if (window.hasOwnProperty("webkitSpeechRecognition")) {
      recognition.continuous = true;
      //recognition.interimResults = false;

      recognition.lang = "en-US";
      recognition.lang = "th-TH";
      recognition.start();

      recognition.onresult = function(e) {
        // console.log(e)
        speech = speech + e.results[0][0].transcript;
        console.log(speech);
      };

      recognition.onend = function(e) {
        console.log("onend");
        console.log(e);
        if (forcestop) {
          forcestop = false;
          this.setState({res:speech})
        } else {
          recognition.start();
        }
      };

      recognition.onerror = function(e) {
        console.log("on error");
        console.log(e);
      };
    } else {
      alert("Must upgrade browser.");
    }
  };
  render() {
    console.log(this.state.res)
    return (
      <div>
        <Button
          content={this.state.btnText ? "Start Record" : "Recording"}
          icon="microphone"
          color={this.state.btnStyle ? "teal" : "red"}
          labelPosition="left"
          onClick={this.btnPressed}
        />
        <Message>
          <Message.Header>Result</Message.Header>
          <p>
            {this.state.res}
          </p>
        </Message>

      </div>
    );
  }
}

export default Speech;
