import React, { Component } from "react";
import {
  Container,
  Icon,
  Grid,
  Header,
  Menu,
  Segment,
  Dropdown,
  List
} from "semantic-ui-react";
import firebase from "./../FirebaseAPI";
import { Prism as SyntaxHighlighter } from "react-syntax-highlighter";
import { atomDark } from "react-syntax-highlighter/dist/esm/styles/prism";
import axios from "axios";

export class RevCode extends Component {
  state = {
    code: `a = int(input())\nb = []\nfor i in range(a):\n\tb.append(i)`,
    userData: { name: "" }
  };
  componentDidMount() {
    const uid = firebase.auth().currentUser.uid;
    const url = "https://revcode.herokuapp.com//userdata?uid=" + uid;
    axios
      .get(url)
      .then(async res => {
        console.log(res.data.userData);
        this.setState({ userData: res.data.userData.user_data });
        this.setState({ userFile: res.data.userData.user_storage });
      })
      .catch(err => {
        alert(err.message);
      });
  }

  render() {
    console.log(this.state);
    return (
      <div>
        <Segment
          inverted
          textAlign="center"
          style={{ padding: "0.5em 0em" }}
          vertical
        >
          <Menu inverted secondary size="large">
            <Menu.Item as="a" header style={{ marginLeft: "2em" }}>
              <Icon name="code" />
              <b>RevCode</b>
            </Menu.Item>

            <Menu.Menu position="right">
              <Dropdown item simple icon="user circle">
                <Dropdown.Menu>
                  <Dropdown.Item
                    text={
                      <span>
                        Signed in as <strong>{this.state.userData.name}</strong>
                      </span>
                    }
                    disabled
                  />
                  <Dropdown.Item
                    text="Sign Out"
                    onClick={() => {
                      firebase.auth().signOut();
                    }}
                  />
                </Dropdown.Menu>
              </Dropdown>
            </Menu.Menu>
          </Menu>
        </Segment>

        <Segment vertical style={{ height: "100vh", padding: "1em 0em" }} inverted>
          <Container >
            <Grid divided stackable>
              <Grid.Column width={3}>
                <Header as="h4" content="Files" />

                  <List divided relaxed>
                    <List.Item>
                      <List.Icon
                        name="github"
                        size="large"
                        verticalAlign="middle"
                      />
                      <List.Content>
                        <List.Header as="a">
                          Semantic-Org/Semantic-UI
                        </List.Header>
                        <List.Description as="a">
                          Updated 10 mins ago
                        </List.Description>
                      </List.Content>
                    </List.Item>
                    <List.Item>
                      <List.Icon
                        name="github"
                        size="large"
                        verticalAlign="middle"
                      />
                      <List.Content>
                        <List.Header as="a">
                          Semantic-Org/Semantic-UI-Docs
                        </List.Header>
                        <List.Description as="a">
                          Updated 22 mins ago
                        </List.Description>
                      </List.Content>
                    </List.Item>
                    <List.Item>
                      <List.Icon
                        name="github"
                        size="large"
                        verticalAlign="middle"
                      />
                      <List.Content>
                        <List.Header as="a">
                          Semantic-Org/Semantic-UI-Meteor
                        </List.Header>
                        <List.Description as="a">
                          Updated 34 mins ago
                        </List.Description>
                      </List.Content>
                    </List.Item>
                  </List>

              </Grid.Column>
              <Grid.Column width={6}>
                <Header as="h4" content="Speech" />
              </Grid.Column>

              <Grid.Column width={7}>
                <Header as="h4" content="Code Display" />
                <SyntaxHighlighter
                  language="python"
                  style={atomDark}
                  wrapLines={true}
                >
                  {this.state.code}
                </SyntaxHighlighter>
              </Grid.Column>
            </Grid>
          </Container>
        </Segment>
      </div>
    );
  }
}

export default RevCode;
