import React, { Component } from "react";

import {
  Button,
  Form,
  Grid,
  Header,
  Message,
  Segment,
  Container,
  Divider
} from "semantic-ui-react";
import "./../Style/Login.css";
import firebase from "firebase/app";
require("firebase/auth");

export class Register extends Component {
  constructor(props) {
    super(props);
    this.state = {
      first: "",
      last: "",
      username: "",
      password: "",
      cpassword: "",
      error: "",
      currentUser: "",
      loginData: {}
    };

    this.handlePassChange = this.handlePassChange.bind(this);
    this.handleUserChange = this.handleUserChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
    this.dismissError = this.dismissError.bind(this);

    var config = {
      apiKey: process.env.REACT_APP_API_KEY,
      authDomain: "https://revcode-83ac0.firebaseapp.com/",
      databaseURL: "https://revcode-83ac0.firebaseio.com/",
      storageBucket: "projectId.appspot.com"
    };

    firebase.initializeApp(config);
  }

  dismissError() {
    this.setState({ error: "" });
  }

  handleSubmit(evt) {
    evt.preventDefault();
    const email = this.state.username;
    const password = this.state.password;
    firebase
      .auth()
      .createUserWithEmailAndPassword(email, password)
      .then(response => {
        // this.setState({
        //   currentUser: response.user
        // });
        console.log("#", response.user);
      })
      .catch(error => {
        this.setState({
          error: error.message
        });
      });
    if (!this.state.first) {
      return this.setState({ error: "First is required" });
    }
    if (!this.state.last) {
      return this.setState({ error: "Last is required" });
    }
    if (!this.state.username) {
      return this.setState({ error: "Username is required" });
    }

    if (!this.state.password) {
      return this.setState({ error: "Password is required" });
    }

    return this.setState({ error: "" });
  }

  handleFirstNameChange(evt) {
    this.setState({
      first: evt.target.value
    });
  }

  handleLastNameChange(evt) {
    this.setState({
      last: evt.target.value
    });
  }

  handleUserChange(evt) {
    this.setState({
      username: evt.target.value
    });
  }

  handlePassChange(evt) {
    this.setState({
      password: evt.target.value
    });
  }

  handleCPassChange(evt) {
    this.setState({
      cpassword: evt.target.value
    });
  }
  render() {
    console.log(this.state);
    return (
      <div className="area">
        <Container className="box" style={{ width: 600 }}>
          <Header as="h2" icon textAlign="center">
            <Header.Content style={{ color: "white" }}>Register</Header.Content>
          </Header>
          <Divider />
          <Form
            inverted
            style={{ paddingTop: 20 }}
            error
            onSubmit={this.handleSubmit}
          >
            {this.state.error && (
              <Message
                onClick={this.dismissError}
                error
                header={this.state.error}
              />
            )}
            <Form.Group widths="equal">
              <Form.Input
                icon="user"
                iconPosition="left"
                fluid
                id="form-subcomponent-shorthand-input-first-name"
                label="First Name"
                placeholder="First Name"
                value={this.state.first}
                onChange={this.handleFirstNameChange}
              />
              <Form.Input
                fluid
                icon="user"
                iconPosition="left"
                id="form-subcomponent-shorthand-input-last-name"
                label="Last Name"
                placeholder="Last Name"
                value={this.state.last}
                onChange={this.handleLastNameChange}
              />
            </Form.Group>
            <Form.Input
              icon="mail"
              iconPosition="left"
              label="Email"
              placeholder="Email"
              value={this.state.username}
              onChange={this.handleUserChange}
            />
            <Form.Input
              icon="lock"
              iconPosition="left"
              label="Password"
              type="password"
              placeholder="Password"
              value={this.state.password}
              onChange={this.handlePassChange}
            />
            <Form.Input
              icon="lock"
              iconPosition="left"
              label="ConfirmPassword"
              type="password"
              placeholder="Confirm Password"
              value={this.state.cpassword}
              onChange={this.handleCPassChange}
            />
            <Grid>
              <Grid.Column textAlign="center">
                <Button
                  type="submit"
                  content="Sign Up"
                  basic
                  inverted
                  color="teal"
                  size="large"
                  style={{ marginTop: "1em" }}
                />
              </Grid.Column>
            </Grid>
          </Form>
        </Container>

        <ul className="circles">
          <li></li>
          <li></li>
          <li></li>
          <li></li>
          <li></li>
          <li></li>
          <li></li>
          <li></li>
          <li></li>
          <li></li>
          <li></li>
        </ul>
      </div>
    );
  }
}

export default Register;
